<!-- cell -->
<template>
    <div @click="click" class="border-color" :style="borderStyle">
        <div :class="['cell', center&&'cell-center', bottom&&'cell-bottom']">
          <slot name="icon">
              <ut-icon v-if="icon" class="cell-icon" :name="icon" size="32" :iconStyle="iconStyle"></ut-icon>
          </slot>
          <div class="cell-content">
            <div class="cell-boday">
              <text v-if="required" class="cell-required">*</text>
              <slot name="title">
                <text v-if="title" class="cell-title" :style="titleStyle">{{title}}</text>
              </slot>
              <slot>
                <text v-if="value" class="cell-value" :style="valueStyle">{{value}}</text>
              </slot>
            </div>
            <slot name="label">
              <text v-if="label" class="cell-label" :style="labelStyle">{{label}}</text>
            </slot>
          </div>
          <slot name="right-icon">
            <ut-icon v-if="isLink" class="cell-icon-right" :name="arrow" size="32" :iconStyle="rightIconStyle"></ut-icon>
          </slot>
        </div>
    </div>
</template>

<script>

import UtIcon from "../ut-icon";

export default {
  components: {UtIcon},
  props: {
      icon: {
          type: String,
          default: ''
      },
      title: {
          type: String,
          default: ''
      },
      label: {
          type: String,
          default: ''
      },
      value: {
          type: String,
          default: ''
      },
      arrowDirection: {
          type: String,
          default: 'right'//left up down
      },
      required: {
          type: Boolean,
          default: false
      },
      isLink: {
          type: Boolean,
          default: false
      },
      center: {
        type: Boolean,
        default: false
      },
      bottom: {
        type: Boolean,
        default: false
      },
      border: {
        type: Boolean,
        default: false
      },
      borderTop: {
        type: Boolean,
        default: false
      },
      borderBottom: {
        type: Boolean,
        default: false
      },
      iconStyle: {
        type: Object,
        default: () => ({color:'#999999'})
      },
      rightIconStyle: {
        type: Object,
        default: () => ({color:'#999999'})
      },
      titleStyle: {
        type: Object,
        default: () => ({})
      },
      valueStyle: {
        type: Object,
        default: () => ({})
      },
      labelStyle: {
        type: Object,
        default: () => ({})
      }
  },
  computed: {
    borderStyle() {
      return {
        borderTopWidth: (this.borderTop||this.border?1:0)+'px',
        borderBottomWidth: (this.borderBottom||this.border?1:0)+'px'
      }
    },
    arrow() {
       switch (this.arrowDirection) {
         case 'left':
           return 'back'
         case 'up':
           return 'up2'
         case 'down':
           return 'down2'
         default:
           return 'arrow';
       }
    }
  },
  methods: {
      click() {
        this.$emit('click');
      }
  }
}
</script>

