  flex: 1;
  background-color: #ffffff;
  padding: 20px 30px;
  flex-direction: row;
  align-items:flex-start;
}
.cell-center {
  align-items: center;
}
.cell-bottom {
  align-items: flex-end;
}
.cell-boday {
  flex-direction: row;
  align-items: center;
  flex: 1;
}
.cell-content {
  flex: 1;
}
.cell-title {
  color: #333333;
  font-size: 28px;
  width: 200px;
  text-overflow: ellipsis;
  lines: 1;
}
.cell-value {
  color: #666666;
  font-size: 28px;
  text-align: right;
  flex: 1;
  text-overflow: ellipsis;
  lines: 1;
  padding-left: 10px;
}
.cell-label {
  color: #999999;
  font-size: 26px;
  flex: 1;
