<!--导航栏.-->
<template>
  <div class="container">
    <div class="background">
      <slot name="background"></slot>
    </div>
    <div v-if="showStatusBar" :style="{ backgroundColor: backgroundColor, height: `${statusBarHeight}px` }"></div>
    <div class="ut-minibar" :style="{ backgroundColor: backgroundColor}" v-if="show">
      <div class="left" @click="leftButtonClicked" aria-label="返回" :accessible="true">
        <slot name="left">
          <ut-icon v-if="showBack" name="back" :style="{ color: textColor }" @click="leftButtonClicked"/>
          <text v-if="leftText" class="icon-text" :style="{ color: textColor }">{{leftText}}</text>
        </slot>
      </div>
      <slot name="middle">
        <text class="middle-title" :style="{ color: textColor }">{{title}}</text>
      </slot>
      <div class="right" @click="rightButtonClicked">
        <slot name="right">
          <text v-if="rightText" class="icon-text" :style="{ color: textColor }">{{rightText}}</text>
        </slot>
      </div>
    </div>
    <slot></slot>
  </div>
</template>

<script>
import Utils from "../utils";
import UtIcon from "../ut-icon";
const navigator = weex.requireModule('navigator');

export default {
  name: 'utNavBar',
  components:{UtIcon},
  props:{
      backgroundColor: {
        type: String,
        default: '#FFFFFF'
      },//导航栏背景色
      textColor: {
        type: String,
        default: '#333333'
      },//文字颜色
      title: {
        type: String,
        default: '标题'
      },//标题
      leftText: {
        type: String,
        default: ''
      },//左侧按钮文字
      rightText: {
        type: String,
        default: ''
      },//右侧按钮文字
      showBack: {
        type: Boolean,
        default: true
      },//是否返回
      show: {
        type: Boolean,
        default: true
      },//是否显示
      showStatusBar: {
        type: Boolean,
        default: true
      }//状态栏显示
  },
  computed: {
    statusBarHeight: function() {
      if (Utils.isIPhoneX()) {
        return 66;
      } else if (Utils.isIOS()) {
        return 40;
      } else {
        return 0;
      }
    },
    screenHeight: function() {
      return Utils.getScreenHeight();
    }
  },
  methods:{
    leftButtonClicked () {
        const self = this;
        if (self.showBack) {
          navigator.pop({}, e => {});
        }
        self.$emit('back', {});
    },
    rightButtonClicked () {
        const self = this;
        if (self.rightText) {
          self.$emit('rightClick', {});
        }
    }
  }
}
</script>

<style scoped>
  .container {
    background-color: #f2f2f2;
    width: 750px;
  }
  .background {
    background-color: #f2f2f2;
    width: 750px;
    top:0;
    left: 0;
    right: 0;
    bottom: 0;
    position:absolute;
  }
  .ut-minibar {
    width: 750px;
    height: 90px;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    background-color: #ffffff;
  }

  .left {
    width: 180px;
    padding-left: 32px;
    flex-direction: row;
    align-items: center;
  }

  .middle-title {
    font-size: 32px;
    color: #ffffff;
    height: 36px;
    line-height: 36px;
    font-weight: bold;
  }

  .right {
    width: 180px;
    padding-right: 32px;
    align-items: flex-end;
  }

  .icon-text {
    font-size: 28px;
    color: #ffffff;
  }
</style>

